import React, { Component } from "react";
import { routerServices } from "../services/RouterServices";
import "bootstrap/dist/css/bootstrap.min.css";
import SideBar from "./SideBar";
import logo from '../intellect Logo.png';

class Dashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      employeeData: this.props.routeData,
    };

    this.redeemBtnHandler = this.redeemBtnHandler.bind(this);
    this.rewardBtnHandler = this.rewardBtnHandler.bind(this);
  }
  redeemBtnHandler() {
    let pointsData = {
      earnedPoints: this.state.employeeData.earnedPoints,
      allocatedPoints: this.state.employeeData.allocatedPoints,
      totalPoints: this.state.employeeData.totalPoints,
    };
    this.props.navigate("/redeem", { state: pointsData });
  }

  rewardBtnHandler() {
    let pointsData = {
      earnedPoints: this.state.employeeData.earnedPoints,
      allocatedPoints: this.state.employeeData.allocatedPoints,
      totalPoints: this.state.employeeData.totalPoints,
    };
    this.props.navigate("/redeem", { state: pointsData });
  }

  render() {
    return (
      <>
        <div className="container dashboard">
          <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <div className="container-fluid">
                <a className="navbar-brand" href="https://www.google.com">
                  <img src={logo} alt="" ></img>
                </a>
                <div
                  className="collapse navbar-collapse"
                  id="navbarSupportedContent"
                >
                  <form className="d-flex">
                    <input
                      className="form-control me-2"
                      type="search"
                      placeholder="Search"
                      aria-label="Search"
                    ></input>
                    <button className="btn btn-outline-success" type="submit">
                      Search
                    </button>
                  </form>
                </div>
              </div>
            </nav>
          </div>
          <div className="side-main">
            <div>
              <SideBar />
            </div>
            <div className="dash-main">
              <div className="greeting">
                <h1>Hello Intellectians !</h1>
              </div>
              <div className="emp-detail">
                <p>Name : {this.state.employeeData.employeeName}</p>
                <p>Employee ID : {this.state.employeeData.employeeId}</p>
                <p>E-mail ID : {this.state.employeeData.employeeEmail}</p>
                <p>Phone No : {this.state.employeeData.employeePhone}</p>
                <p>Total Points : {this.state.employeeData.totalPoints}</p>
              </div>
              <div>
                <button onClick={this.redeemBtnHandler}>Redeem</button>
                <button onClick={this.rewardBtnHandler}>Reward</button>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}
export default routerServices(Dashboard);
