import React, { Component } from "react";
import { routerServices } from "../services/RouterServices";

class Dashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      employeeData: this.props.routeData,
    };

    this.redeemBtnHandler = this.redeemBtnHandler.bind(this);
    this.rewardBtnHandler = this.rewardBtnHandler.bind(this);
  }
  redeemBtnHandler() {
    let pointsData = {
      earnedPoints: this.state.employeeData.earnedPoints,
      allocatedPoints: this.state.employeeData.allocatedPoints,
      totalPoints: this.state.employeeData.totalPoints,
    };
    this.props.navigate("/redeem", { state: pointsData });
  }

  rewardBtnHandler() {
    let pointsData = {
      earnedPoints: this.state.employeeData.earnedPoints,
      allocatedPoints: this.state.employeeData.allocatedPoints,
      totalPoints: this.state.employeeData.totalPoints,
    };
    this.props.navigate("/redeem", { state: pointsData });
  }

  render() {
    return (
      <><div class="container-fluid">
      <a class="navbar-brand" href="#">Navbar</a>
      
      <div class="collapse navbar-collapse" id="navbarSupportedContent">
        
        <form class="d-flex">
          <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"></input>
          <button class="btn btn-outline-success" type="submit">Search</button>
        </form>
      </div>
    </div>
        <div>
          <h1>Dashboard</h1>
        </div>
        <div>
          <h3>My Profile</h3>
          <p>Name : {this.state.employeeData.employeeName}</p>
          <p>Employee ID : {this.state.employeeData.employeeId}</p>
          <p>E-mail ID : {this.state.employeeData.employeeEmail}</p>
          <p>Phone No : {this.state.employeeData.employeePhone}</p>
          <p>Total Points : {this.state.employeeData.totalPoints}</p>
        </div>
        <div>
          <button onClick={this.redeemBtnHandler}>Redeem</button>
          <button onClick={this.rewardBtnHandler}>Reward</button>
        </div>
      </>
    );
  }
}
export default routerServices(Dashboard);
